CREATE TABLE "TBLU_IFRS_CUSTOMER_RATING" (
    "PKID" BIGINT GENERATED BY DEFAULT AS IDENTITY (
        START WITH 1
        INCREMENT BY 1
        MINVALUE 1
        MAXVALUE 9999999999999999999
        CACHE 20
    ) NOT NULL,

    "DTE_EFFT" DATE NOT NULL,
    "IDN_PARTY" VARCHAR(50),
    "CDE_RATING_COMPANY" VARCHAR(2) NOT NULL,
    "CDE_DEBT_RATING" VARCHAR(2),
    "DTE_RATING" DATE,

    "SYSCODE_CUSTOMER_RATING" VARCHAR(25) NOT NULL,
    "CODE_APP" VARCHAR(20) NOT NULL,

    "CREATED_BY" VARCHAR(50) NOT NULL,
    "CREATED_DATE" DATE NOT NULL,
    "CREATED_HOST" VARCHAR(50) NOT NULL,

    "UPDATED_BY" VARCHAR(50),
    "UPDATED_DATE" DATE,
    "UPDATED_HOST" VARCHAR(50),

    "IS_DRAFT" NUMERIC(1,0) DEFAULT 0 NOT NULL,
    "IS_DELETED" NUMERIC(1,0) DEFAULT 0 NOT NULL,

    "DELETED_BY" VARCHAR(50),
    "DELETED_DATE" TIMESTAMP(6),
    "DELETED_HOST" VARCHAR(50),

    CONSTRAINT "PK_IFRS_CUST_RATING" PRIMARY KEY ("PKID")
);
