CREATE TABLE "TBLU_IFRS_PDEXTERNAL" (
    "PKID" BIGINT GENERATED BY DEFAULT AS IDENTITY (
        START WITH 1
        INCREMENT BY 1
        MINVALUE 1
        MAXVALUE 99999999999
        CACHE 20
    ) NOT NULL,

    "EFFECTIVE_DATE" DATE NOT NULL,
    "RATING" VARCHAR(2) NOT NULL,
    "TTC_PD" NUMERIC(18,2) NOT NULL,
    "SYSCODE_PD_EXTERNAL" VARCHAR(25) NOT NULL,
    "CODE_APP" VARCHAR(20) NOT NULL,

    "CREATED_BY" VARCHAR(50) NOT NULL,
    "CREATED_DATE" DATE NOT NULL,
    "CREATED_HOST" VARCHAR(50),

    "UPDATED_BY" VARCHAR(50),
    "UPDATED_DATE" DATE,
    "UPDATED_HOST" VARCHAR(50),

    "IS_DRAFT" NUMERIC(1,0) DEFAULT 0 NOT NULL,
    "IS_DELETED" NUMERIC(1,0) DEFAULT 0 NOT NULL,

    "DELETED_BY" VARCHAR(50),
    "DELETED_DATE" TIMESTAMP(6),
    "DELETED_HOST" VARCHAR(50),

    CONSTRAINT "PK_IFRS_PD_EXT" PRIMARY KEY ("PKID")
);
